import './App.css'
import React from 'react'
import {
  BrowserRouter as Router,
  Switch,
  NavLink,
  Route,
  Link
} from 'react-router-dom'
import { Redirect } from 'react-router'
import Register from './pages/register'
import StatePage from './pages/statePage'
import Message from './pages/message'
import { Button } from 'antd'
import 'antd/dist/antd.css'
import './assets/less/message.less'

import Nav from './component/nav'

// function clickfun(){
// // 其中this为undefined,可以定义全局that为类的构造器this
//   console.log(that.state.ishot)
// }

{
  /* Link组件，只改变url */
}
// 简单组件
function App () {
  return (
    <div className='App'>
      <header className='App-header'>
        {/* <Button type="primary">Button</Button> */}
        <Router>
          {/* 静态li路由导航 */}
          <ul>
            {/* （1）Link 静态导航标签 没有样式*/}{' '}
            {/* <li><Link to='/statePage'>我的</Link></li> */}{' '}
            {/* link隐形传递参数 */}{' '}
            <Link
              to={{
                pathname: '/statePage',
                search: '?username=name',
                hash: '#the-hash',
                state: {
                  fromDashboard: true,
                  flag: '测试测试'
                } // 隐藏属性 可以传递隐蔽的东西
              }}
            >
              我的
            </Link>
            {/* （2）NavLink 选中添加样式 */}{' '}
            <li>
              {' '}
              <NavLink
                to='/register'
                activeClassName='selected'
                activeStyle={{
                  color: 'red'
                }}
              >
                {' '}
                登陆{' '}
              </NavLink>
            </li>
          </ul>{' '}
          {/* url 中拼写地址 */} {/* render传值 */}{' '}
          <Route
            path='/statePage'
            exact
            render={() => {
              // return 你要显示的视图
              return <h1> 瑶瑶加油 </h1>
              //  return <IndexPage shop={shop} setShop={setShop}/>
            }}
          />
          <Route path='/statePage' exact component={StatePage}>
           state页面
          </Route>
          <Route path='/register' exact component={Register}>
          props页面
          </Route>{' '}
          {/* 重定向： <Redirect from="messages/:id" to="/messages/:id" /> */}
        </Router>
      </header>{' '}
    </div>
  )
}

// 复杂组件 复杂的调用方式，简化版查看statePage
let that

class ClassCom extends React.Component {
  //构造器调用一次
  constructor (props) {
    //构造器是否接受props，是否传递props，取决于是否希望在构造器中通过this访问props
    super(props)

    this.state = {
      ishot: true,
      name: 'yaoyao'
    } //（通过this.state初始化实力对象）
    // that=this;
    this.changeHot = this.changeHot.bind(this) //将更改this，并给自身增加一个changeHot方法(为事件处理函数绑定实例)
    // this.demo=this.changeHot.bind(this);//将更改this，
  }

  //点几次调用几次
  changeHot () {
    //这里指的this为undefined，因为类中的方法默认开启严格模式
    // changeHot是作为onclick的回调，所以不是通过实例调用的，是直接调用
    // changeHot放在ClassCom的原型对象上，供实例调用
    console.log(this.state.ishot)

    // this.state.ishot=!this.state.ishot;！！！状态state不可直接更改，需要使用setState
    const ishot = this.state.ishot //更新为合并不是替换
    this.setState({
      ishot: !ishot
    })
  }

  // render调用1+n次，1是初始化，N是状态更新的次数
  render () {
    console.log(this)
    const { ishot, name } = this.state
    const msg = {
      title: 'topic',
      content: 'this is a piece of information',
      time: 2019
    }

    return (
      <div>
        <h2>
          直接用this.state中的值： {this.state.name}
          {this.state.ishot ? 'hh' : 'xx'}
        </h2>
        <h2>
          先定义再用this.state的值： {name}: {ishot ? 'hh' : 'xx'}
        </h2>
        {/* <h1 onClick={clickfun} className="h1Sty"> 实例的方法，定义全局that调用</h1> */}
        <h1 onClick={this.changeHot}> 实例的方法 </h1>
        {/* <h1 onClick={this.demo()}>实例的方法</h1> */}
        {/* 直接引用某组件 */} <statePage />
        {/* 引用自组件，并通过父组件props传参 */} {/* (1)...复制对象 可批量*/}
        <Message {...msg} />
        {/* （2）传递单个对象 */} <Message content='内容' time='2017' />
        <Message content='内容，不传时间设置默认值' />
        <Message title='乙' content='内容' time={2019} />
        {/* ref */} <Register />
      </div>
    )
  }
}
export default ClassCom
// export default App;
