import React, { Component } from 'react';

class Register extends Component{
    showData =()=>{
        console.log(this.refs.inputInfo);

        const {inputInfo}=this.refs;
        console.log(inputInfo.value)

    }
    showData2=()=>{
        const outputInfo =this.refs;
        console.log(outputInfo.value)
    }
    render(){
        return (<div>
            <input ref="inputInfo" type="text" placeholder="点击按照展示输入信息"></input>
            <button onClick={this.showData}>展示</button>
            <input ref="outputInfo" onBlur={this.showData2} type="text" placeholder="点失去焦点展示信息"></input>
        </div>)
    }
}
export default Register;
