// ref使用
class Register extends React.Component {
  showData = () => {
    console.log(this.inputInfo.value)
  }
  showData2 = () => {
    const { outputInfo } = this
    console.log(outputInfo.value)
  }
  render () {
    return (
      <div>
        {/* 回调函数最基础的定义就是你定义了一个函数，但是你没有调用，被别人使用 */}
        <input
          ref={currentNode => {
            console.log(currentNode) //回调函数是谁调用就是谁
            this.inputInfo = currentNode
          }}
          placeholder='点击按钮打印输入的信息'
        ></input>
        <button onClick={this.showData}>点击显示</button>

        <input
          onBlur={this.showData2}
          ref={currentNode => {
            this.outputInfo = currentNode
          }}
          placeholder='失去焦点打印输入信息'
        ></input>
      </div>
    )
  }
}
export default Register
