import './App.css';
import React from 'react';
import {
  BrowserRouter as Router,
  Switch,NavLink,
  Route,
  Link
} from "react-router-dom";
import {Redirect} from 'react-router';
import Register from './pages/register';
import Login from './pages/login'
import Messages from './pages/message';
import { Button } from 'antd';
import 'antd/dist/antd.css';
import './assets/less/message.less'

import Nav from './component/nav';

{/* Link组件，只改变url */}
function App() {
  return (
    <div className="App">
      <header className="App-header">
      {/* <Button type="primary">Button</Button> */}
       <Router>
          {/* 静态li路由导航 */}
         <ul>
            {/* （1）Link 静态导航标签 没有样式*/}
            {/* <li><Link to='/login'>我的</Link></li> */}
            {/* link隐形传递参数 */}
            <Link
              to={{
                pathname: "/login",
                search: "?username=name",
                hash: "#the-hash",
                state: { fromDashboard: true, flag:'测试测试' }  // 隐藏属性 可以传递隐蔽的东西
              }}
            >我的</Link>

            {/* （2）NavLink 选中添加样式 */}
            <li><NavLink to='/register' activeClassName="selected" activeStyle={{color: 'red'}} > 登陆</NavLink></li>
        </ul>
          {/* url 中拼写地址 */}
        {/* render传值 */}
        <Route path="/login" exact render={()=>{
           // return 你要显示的视图
           return <h1>瑶瑶加油</h1>
          //  return <IndexPage shop={shop} setShop={setShop}/>

        }}/>

        <Route path='/login' exact component={Login} >。。</Route>
        <Route path='/register' exact component={Register}>？？</Route>
       {/* 重定向： <Redirect from="messages/:id" to="/messages/:id" /> */}

      </Router>
      {/* <Switch>

        <Route exact path='/messages/:id' component={Messages}></Route> 

        <Route exact path='/messages/:id?' component={Messages}></Route> 
      </Switch> */}
      </header>
    </div>
  );
}
export default App;
