import React from 'react'
class LifeCycle extends React.Component {
  // 构造器可以不写
  //   constructor (props) {
  //     super(props)
  //     this.state = {
  //       title: 'hello'
  //     }
  //   }
  state = {
    title: 'hello'
  }
  showData = event => {
    this.setState({
      title: event.target.value
    })
  }
  render () {
    const { title } = this.state
    return (
      <div>
        <input
          placeholder='失去聚焦在p标签显示信息'
          onBlur={this.showData}
        ></input>{' '}
        <p>: {title} </p>{' '}
      </div>
    )
  }
}
//最重要的是三个：render（初始化渲染）、componentDidMount（挂载完成）、componentWillUnmount（即将卸载）
// 父组件
class A extends React.Component {
  state = {
    fatherName: 'father'
  }
  transChange = () => {
    const { fatherName } = this.state
    this.setState({
      fatherName: 'newName'
    })
  }
  //不更新状态下强制修改
  forceChange = () => {
    this.forceUpdate()
  }
  //组件将要挂载
  UNSAFE_componentWillMount () {
    console.log('A---UNSAFE_componentWillMount')
  }
  //组件将要卸载
  componentWillUnmount () {
    console.log('A---componentWillUnmount ---import')
    //这里可以做收尾工作。如：清空定时器、取消订阅消息等
  }
  // 是否修改：控制组件更新的阀门
  shouldComponentUpdate () {
    console.log('A---shouldComponentUpdate')
    return false //这里默认为true，如果自行修改一定要返回一个boolean值判断是否接着执行。true：接着执行；false：不执行（此时可以考虑强制执行）
    //这里一般为修改里state
  }

  //强制更新
  UNSAFE_componentWillUpdate () {
    console.log('A---componentWillUpdate')
  }
  //   组件更新完毕 （如果传props和state的话是最新渲染之前的即是之前的prop和state值，再传一个snapshotu快照）
  componentDidUpdate (preProps, prestate, snapShot) {
    console.log('A---componentDidUpdate')
  }
  //   挂载完成
  componentDidMount () {
    console.log('A---componentDidMount   ---import')
    //这里做初始化工作：如：发送网络请求、做定时器、订阅消息等
  }
  // 若state的值在任何时候都取决于props，那么可以使用getDerivedStateFromProps
  static getDerivedStateFromProps (props, state) {
    console.log(props, state)
    return null
  }
  // 在更新之前获取快照
  getSnapshotBeforeUpdate () {
    console.log('getSnapshotBeforeUpdate')
    return '快照'
  }
  render () {
    console.log('A---render  ---import ')
    //触发，做一次初次渲染
    return (
      <div>
        <p onClick={this.transChange}> 这是父亲的值 {this.state.fatherName} </p>{' '}
        <B fatherName={this.state.fatherName} />{' '}
        <button onClick={this.forceChange}> 不更新任何状态下强制更新 </button>{' '}
      </div>
    )
  }
}
// 子组件
class B extends React.Component {
  shouldComponentUpdate () {
    return true //这里默认为true，如果自行修改一定要返回一个boolean值判断是否接着执行
    // 组件修改
    console.log('B---shouldComponentUpdate')
  }
  componentDidMount () {
    // 挂载完成
    console.log('B---componentDidMount  ---import')
  }

  render () {
    console.log('B---render')
    const { fatherName } = this.props
    return (
      <div>
        <p> 这是儿子收到的 {fatherName} </p>{' '}
      </div>
    )
  }
}

export default A
