import React from 'react'
class UnControlPage extends React.Component {
  submitFrom = event => {
    event.preventDefault() //阻止表单的提交
    // console.log(this.username.value + '----' + this.pwd.value)

    const { username, pwd } = this;
    // console.log(username.value + '----' + pwd.value)
alert(`输入的用户名是${username.value}，输入的密码是${pwd.value}`)
  }
  render () {
    return (
      // 非受控组件
      <form action='' onSubmit={this.submitFrom}>
          <p>非受控组件</p>
        <input
          ref={currentNode => {
            //ref是这个节点
            this.username = currentNode
          }}
          placeholder='输入用户名'
          type='text'
        ></input>
        <input
          ref={currentNode => {
            //ref是这个节点
            this.pwd = currentNode
          }}
          type='password'
          placeholder='密码'
        ></input>
        <button>提交</button>
      </form>
    )
  }
}
export default UnControlPage;
