import React from 'react';
class ControlCon extends React.Component {
    //受控组件就是现用现取,将数据存到state，所以一般采用受控组件，优点就是省略ref,类似vue的数据双向绑定
  state = {
    username: '',
    pwd: ''
  }
  saveUsername = event => {
    console.log(event.target.value)
    this.setState({
      username: event.target.value
    })
  }
  savePwd = event => {
    console.log(event.target.value)
    this.setState({
        pwd: event.target.value
      })
  }
  submitFrom = event => {
    event.preventDefault() //阻止请求
    const {username,pwd}=this.state;
    console.log(username+"----"+pwd)
  }
  render () {
    return (
      <form action='' onSubmit={this.submitFrom}>
        <input onChange={this.saveUsername} placeholder='输入用户名' type="text"></input>
        <input onChange={this.savePwd} placeholder='输入密码' type="password"></input>
        <button> 提交 </button>
      </form>
    )
  }
}
export default ControlCon;
