import React from 'react'

//#region
/* 高阶函数：满足两条件之一就是高阶函数
    1:函数A接收到的参数是一个函数，那么他就是一个高阶函数
    2:函数A调用的返回值依然是一个函数，那么函数A就是一个高阶函数。常见示例：promise、setTimeout、ayy.map()等

   函数的柯里化：通过函数调用继续返回函数的形式，实现多次接收函数参数最后统一处理的函数编码形式
*/
//#region

class ControlCon extends React.Component {
  //受控组件就是现用现取,将数据存到state，所以一般采用受控组件，优点就是省略ref,类似vue的数据双向绑定
  state = {
    username: '',
    pwd: ''
  }
//   保存用户名到状态
  saveUsername = event => {
    console.log(event.target.value)
    this.setState({
      username: event.target.value
    })
  }
//   保存密码到状态
  savePwd = event => {
    console.log(event.target.value)
    this.setState({
      pwd: event.target.value
    })
  }
//   保存升级
  submitFrom = event => {
    event.preventDefault() //阻止请求
    const { username, pwd } = this.state
    console.log(username + '----' + pwd)
  }
  render () {
    return (
      <form action='' onSubmit={this.submitFrom}>
        <input
          onChange={this.saveUsername}
          placeholder='输入用户名'
          type='text'
        ></input>
        <input
          onChange={this.savePwd}
          placeholder='输入密码'
          type='password'
        ></input>
        <button> 提交 </button>
      </form>
    )
  }
}
export default ControlCon
