import React from 'react'
class LifeCycle extends React.Component {
  state = {
    title: 'hello'
  }
  showData = event => {
    this.setState({
      title: event.target.value
    })
  }
  render () {
      const {title}=this.state
    return (
      <div>
        <input placeholder='失去聚焦在p标签显示信息' onBlur={this.showData}></input>
        <p>:{title}</p>
      </div>
    )
  }
}
export default LifeCycle;
