import React, {
    Component
} from "react";
// import { Menu, Dropdown, Button } from 'antd';
// 复杂组件 相比app.js简化写法
class StatePage extends Component {
    // 初始化状态
    state = {
        name: "yaoyao",
        age: 20
    }
    changeAge = () => {
        let age=this.state.age;
        this.setState({
            age:30
        })
    }
    render() {
        return (
        <div>
            <p > {this.state.name}:{this.state.age} </p>
            <button onClick={this.changeAge}>    点击修改年龄</button>
        </div>
        )
    }
}
export default StatePage;
